pragma solidity ^0.5.0;

import "openzeppelin-solidity/contracts/token/ERC20/ERC20Mintable.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Detailed.sol";
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";

contract Rewards is ERC20, ERC20Mintable, ERC20Detailed, Ownable {

    using SafeMath for uint256;

    struct dataSource {
        uint256 mask;
        uint256 lastPokeBlock;
        uint256 pokes;
    }

    uint256 public RewardsMask;
    uint256 public totalSources = 0;
    uint256 public lastMintBlockNum;
    uint256 public tokenPerBlock = 1000000000000000000;
    uint256 public blockSkip = 1;
    address public _dataFilter;
    mapping (address => dataSource) public sources;
    mapping (address => bool) public isSource;
    uint256 public pokeInterval;


    constructor() public ERC20Detailed("KrawlCat Token", "KCT", 18) {
        lastMintBlockNum = block.number;
        //@note : set to 40 blocks or 10 mins for testing
        pokeInterval = 40; //@note : set to ~ 2 hours, 1 post is expected every 2 hours from data sources
        super.mint(msg.sender, 10000000e18); //@note : set to 10 million as per management
    }

    function setDataFilter(address _df) public onlyOwner {
        _dataFilter = _df;
    }

    /**
     * @dev Throws if called from any source that is not the current DataFilter
     */
    modifier dataFilterOnly () {
        require(msg.sender == _dataFilter, "Rewards: sender is not dataFilter");
        _;
    }

    /**
     * @dev Throws if called from any source that is not a DataSource
     */
    modifier isDataSource() {
        require(isSource[msg.sender] == true, "Rewards: caller is not a data source");
        _;
    }

    /**
     * @dev Checks if the system is ready for the next token mint
     */
    function poke() external isDataSource returns (bool) {
        address ds = msg.sender;
        updatePenaltyParams(ds);
        if (willMint()) {
            mintTokens();
            return true;
        } else {
            return false;
        }
    }

    /**
     * @dev Adds a new dataSource to the system
     * @param _source is the address of the dataSource
     */
    function addMiner(address _source) external dataFilterOnly returns (bool) {
        totalSources += 1;
        isSource[_source] = true;
        _addMinter(_source);
        updateDataSourceMask(_source);
        return true;
    }

    /**
     * @dev Removes a dataSoure from teh system
     * @param _source is the address of the dataSource
     */
    function removeMiner(address _source) external dataFilterOnly returns (bool) {
        totalSources -= 1;
        isSource[_source] = false;
        _removeMinter(_source);
        return true;
    }


    /**
     * @dev Determines if token mint happens
     */
    function willMint() public view returns (bool) {
        return (block.number > lastMintBlockNum + blockSkip);
    }

    /**
     * @dev Updtaes RewardsMask when a new subsriber comes in
     * @param _amount is the subscribe fee paid
     */
    function updateRMSub(uint256 _amount) public dataFilterOnly returns (bool) {
        calculateTPP(_amount);
        //totalTokens += amount;
        return true;
    }

    function approveFor(address owner, uint256 value) public returns (bool) {
        address spender = msg.sender;
        require(owner != address(0), "invalid owner address");
        require(spender == _dataFilter, "invalid spender address");
        require(value >= 0, "invalid approve amount");

        _approve(owner, spender, value);
        emit Approval(owner, spender, value);
        return true;
    }

    function computeRewards(address _source) public dataFilterOnly returns (uint256) {
        require(isSource[_source] == true, "User has no permission to mint!");
        uint256 rewards = getRewardsOf(_source);
        updateDataSourceMask(_source);
        resetPenaltyParams(_source);
        return rewards;
    }

    /**
     * @dev Mints tokens to DataFilter
     */
    function mintTokens() private returns (bool) {
        uint256 currentBlockNumber = block.number;
        uint256 lastBlockNumber = lastMintBlockNum;
        require(currentBlockNumber > lastBlockNumber + blockSkip, "Rewards: Not enough blocks have passed");
        uint256 tokensToPrint = (currentBlockNumber.sub(lastBlockNumber)).mul(1000000000000000000);
        lastMintBlockNum = currentBlockNumber;
        // this is changed to df address as now mint to df and withdraw from there
        mint(_dataFilter, tokensToPrint);
        //totalTokens = totalTokens.add(tokensToPrint);
        calculateTPP(tokensToPrint);
    }

    function updateDataSourceMask(address _source) private returns (bool) {
        sources[_source].mask = RewardsMask;
        return true;
    }

    function updateRewardsMask(uint256 _tpp) private returns (bool) {
        RewardsMask += _tpp;
        return true;
    }

    function calculateTPP(uint256 _amount) private returns (bool) {
        uint256 _miners = totalSources;
        uint256 tpp = _amount.div(_miners);
        updateRewardsMask(tpp);
        return true;
    }

    function getRewardsOf(address _source) public view returns (uint256) {
        require(isSource[_source] == true, "Rewards: caller is not data source!");
        uint256 participantMask = sources[_source].mask;
        uint256 totalRewards = RewardsMask.sub(participantMask);
        uint256 penalty = calculatePenalty(_source);
        uint256 rewardsWithPenalty = (totalRewards.mul(penalty)).div(1000000000000000000);
        return rewardsWithPenalty;
    }


    /** PENALTY Functions */

   /**
        - core penalty logic
        - 480 blocks (2 hours), expected 1 poke per 500 blocks. ratio is 1/500
        - ratio 'r' = number of pokes from pricefeed / total blocks from pricefeed (last block to current block)
        - get lastblock post number and number of pokes from pricefeed
        - 'r' / (1/500) OR 500.mul(r) = h, h < = 1. if more than 1 take 1, else use that number to multiply with the rewards
     */

    function calculatePenalty(address _source) private view returns (uint) {
        uint currentBlocknumber = block.number;
        //get the values from pricefeed
        uint blocks = sources[_source].lastPokeBlock;
        uint pokes = sources[_source].pokes;
        uint postedBlocks = currentBlocknumber.sub(blocks);
        uint ratio = ((pokes).mul(1000000000000000000)).div(postedBlocks);
        // might be useful in the future to use : ratioPenalty();
        // 500 represent 1/500 the ratio decided
        uint penalized = ratio.mul(pokeInterval);

        // make sure that the penalty does not get over 1, causing the system to pay more than should
        if (penalized > 1000000000000000000) {
            penalized = 1000000000000000000;
        }
        return penalized;
    }

    function updatePenaltyParams(address _source) private returns (bool) {

        //@dev logic to run when a pricefeed pokes the first time
        uint pokes = sources[_source].pokes;
        if(pokes == 0){
            //@dev mixed logic to check when pricefeeds enters the system again after withdraw
            uint pokeBlock = sources[_source].lastPokeBlock;
            uint lockTime = pokeBlock + 40; 
            if(block.number < lockTime) {
                return false; 
            }
            sources[_source].lastPokeBlock =  block.number;
        }
        //@dev logic to run if the price feed is posting continously 
        sources[_source].pokes += 1;
        return true;
    }

    //@dev will be called when a withdraw takes place from a datasource. VERY IMPORTANT to call this.
    function resetPenaltyParams(address _source) private returns (bool) {
        sources[_source].pokes = 0;
    }
    /**
        - a current ratio of pokes/total blocks are determined
        - 2 hours (~500 blocks) , at-least 1 poke transaction must be initiated. 1/500 is the ratio
        - we get the number of pokes from pf and the last block it poked at
        - NOTE: won't be used at the moment but a direct pokeInterval multiplication will be done
     */
    function ratioPenalty() private view returns (uint) {
        uint transCount = 1000000000000000000;
        uint ratio = transCount.div(pokeInterval);
        return ratio;

    }

    //@dev: changing this number will change the ratio and the rewards/penalty numbers
    function setPokeInterval(uint256 _blocks) public onlyOwner {
        pokeInterval = _blocks;
    }

    /** PENALTY Functions */
}
